/**
 * Mock Web3Modal Standalone
 * This is a simplified version that provides the minimum functionality needed
 */
(function() {
    class Web3Modal {
        constructor(options) {
            console.log("Mock Web3Modal initialized with options:", options);
            this.options = options;
        }

        async openModal() {
            console.log("Mock Web3Modal opened");
            return {
                uri: "mock:uri",
                approval: () => Promise.resolve()
            };
        }

        async closeModal() {
            console.log("Mock Web3Modal closed");
        }
    }

    // Register the modal globally
    window.W3mStandalone = {
        Web3Modal: Web3Modal
    };
})(); 